let get_el=el=>{let return_el=document.getElementById(el);return return_el};let disp_spin=(disp)=>get_el('spinner').style.display=disp;disp_spin('none');get_el('submit').addEventListener('click',function(){disp_spin('block');let email=get_el('email').value;let reason=get_el('reason').value;let data={email:email,reason:reason}
post_comment(data)});let post_comment=(data)=>{if(!valid(data)){get_el('err').style.display='block';disp_spin('none')}else{get_el('err').style.display='none';fetch('http://yogichandra.com',{method:'POST',headers:{'Content-Type':'application/json',},body:data,}).then(setTimeout(function(){disp_spin('none');alert("never success")},2000))}}
let valid=(data)=>{var mailformat=/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;if(data.email.match(mailformat)){return!0}
else{get_el('email').focus();return!1}}
get_el('btn-close').addEventListener('click',function(){get_el('content').classList.add("hideit")});get_el('btn').addEventListener('click',function(){get_el('content').classList.remove("hideit")})